function dataFormatter(obj) {
    var pList = ['保险业','采矿','能源','餐饮','宾馆','房地产','服务','求助','咨询','举报'];
    var temp;
    for (var year = 1; year <= 12; year++) {
        var max = 0;
        var sum = 0;
        temp = obj[year];
        for (var i = 0, l = temp.length; i < l; i++) {
            max = Math.max(max, temp[i]);
            sum += temp[i];
            obj[year][i] = {
                name : pList[i],
                value : temp[i]
            }
        }
        obj[year + 'max'] = Math.floor(max / 100) * 100;
        obj[year + 'sum'] = sum;
    }
    return obj;
}
