     var highlightInfo = {
        seriesIndex: -1,
        dataIndex: -1,
        name: null,
        enabled: function (value) {
            if (arguments.length == 0)
                return this.seriesIndex != -1 && this.dataIndex != -1;

            if (!value) {
                this.seriesIndex = -1;
                this.dataIndex = -1;
                this.name = null;
            }
        }
    };

    //高亮函数
    function highlight(target) {
        // target is name
        if (typeof target === "string") {
            option.series.forEach(function (series, i) {
                series.data.forEach(function (d, j) {
                    if (d.name == target) {
                        highlightInfo.seriesIndex = i;
                        highlightInfo.dataIndex = j;
                        return false;

                    }
                });
            });
            if (highlightInfo.enabled()) {
                mychart.dispatchAction({
                    type: 'highlight',
                    seriesIndex: highlightInfo.seriesIndex,
                    name: target
                })
            }
        }
            // target is data
        else if (typeof target === "object") {
            var highlightSeries = option.series[option.series.length - 1];
            highlightSeries.data.push({
                name: target.name,
                value: [target.x, target.y]
            });
            mychart.setOption(option);
        }
    }

    function unhighlight() {
        if (highlightInfo.enabled()) {
            mychart.dispatchAction({
                type: 'downplay',
                seriesIndex: highlightInfo.seriesIndex,
                name: highlightInfo.name
            });
            highlightInfo.enabled(false);
        }
        var highlightSeries = option.series[option.series.length - 1];
        highlightSeries.data = [];
        mychart.setOption(option);
    }

